import Alarm from 'react-native-alarm';

const alarmDetails = {
    id: 'my_alarm',  // a unique ID for the alarm
    message: 'Wake up!',  // the message to display when the alarm goes off
    playSound: true,  // whether to play a sound when the alarm goes off
    soundName: 'default',  // the name of the sound to play (optional)
    schedule: {  // the schedule for the alarm
      hour: 8,  // the hour (0-23)
      minute: 0  // the minute (0-59)
    }
  };
  
  Alarm.scheduleAlarm(alarmDetails);
