import { 
    StyleSheet, 
    TextInput ,
    View,
    KeyboardAvoidingView,
    Text,
} from 'react-native';
import React,{useState,useEffect} from 'react'

const Register = () => {
    return (
        <KeyboardAvoidingView 
            style={styles.container}
            behavior="padding">
            <View style={styles.inputContainer}>
            <View style={styles.header}>
                <Text style={styles.textHeader}>Account Login</Text>
            </View>
                <TextInput
                    placeholder="Email" 
                    value={email} 
                    onChangeText={text=>setEmail(text)}
                    style={styles.input}/>
                    
                <TextInput 
                    placeholder="Password" 
                    value={password} 
                    onChangeText={text=>setPassword(text)} 
                    style={styles.input}
                    secureTextEntry={true}/>
            </View>
            
            <View style={styles.buttonContainer}>
                <TouchableOpacity 
                    onPress={register}          
                    style={styles.button}
                    >
                    <Text style={styles.buttonText}>Register</Text>
                </TouchableOpacity>
    
                <TouchableOpacity 
                    onPress={SignInUser}
                    style={[styles.button,styles.buttonOutline]}>
                    <Text style={styles.buttonOutlineText}>Login</Text>
                    
                </TouchableOpacity>
            </View>
        </KeyboardAvoidingView>
      )
    }
    
    export default Register;

    const styles = StyleSheet.create({
        container:{
            flex:1,
            justifyContent:'center',
            alignItems: 'center',
        },
        header:{
            alignItems: 'center',
            marginBottom:10,
            fontSize:20,
            color:'red',
        },
        inputContainer:{
            width:'80%',
        },
        input:{
            backgroundColor:'white',
            paddingHorizontal:15,
            paddingVertical:10,
            borderRadius:10,
            marginTop:5,
        },
        buttonContainer:{
            width:'60%',
            justifyContent:'center',
            alignItems: 'center',
            marginTop:40,
    
        },
        button:{
            backgroundColor:'#0782f9',
            width:'100%',
            padding:15,
            borderRadius:10,
            alignItems: 'center',
    
        },
        buttonOutline:{
            backgroundColor:'white',
            marginTop:5,
            borderColor:'#0782f9',
            borderWidth:2,
        },
        buttonText:{
            color:'white',
            fontWidth:'700',
            fontSize:16,
        },
        buttonOutlineText:{
            color:'#0782f9',
            fontWidth:'700',
            fontSize:16,
        },
        icon:{
            fontSize:'17%',
            marginTop:10,
        },
        textHeader:{
            fontSize:20,
            fontWeight:'100',
            letterSpacing:1,
    
        },
    })
