import React, {useState, useEffect} from 'react';
import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  Image,
  ToastAndroid,
} from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { Items} from '../database/Database';
import { COLORS } from '../assets/Style';
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons';
import styles from '../assets/Style';


const MyCart = ({navigation}) =>{

    const [product, setProduct] = useState();
    const [total , setTotal] = useState(null);

    useEffect(()=>{
        const unsubscribe = navigation.addListener('focus',()=>{
            getDataFromDB();
        });
        return unsubscribe;
    }, [navigation]);

    const getDataFromDB = async() =>{
        let items = await AsyncStorage.getItem('cartItems')
        items = JSON.parse(items);
        let productData = [];
        if(items){
            Items.forEach(data =>{
                if(items.includes(data.id)){
                    productData.push(data)
                    return 
                }
            })
            setProduct(productData)
            getTotal(productData)
        }else{
            setProduct(false)
            getTotal(false)
        }
    };
    const getTotal = productData =>{
        let total = 0;
        for (let index = 0; index < productData.length; index++) {
            let productPrice = productData[index].productPrice
            total = total + productPrice; 
        }
        setTotal(total);
    }

// remove data from Cart

const removeItemFromCart = async id => {
    let itemArray = await AsyncStorage.getItem('cartItems');
    itemArray = JSON.parse(itemArray);
    if(itemArray) {
        let array = itemArray;
        for (let index = 0; index < array.length; index++) {
            if(array[index] == id){
                array.splice(index, 1)
            }
            await AsyncStorage.setItem('cartItems',JSON.stringify(array));
            getDataFromDB();
        }
    }
}
 
// checkOut

const checkOut = async () =>{
    try {
        await AsyncStorage.removeItem('cartItems')
    } catch (error) {
        return error
    }

    ToastAndroid.show("Items will be Delivers SOON!" , ToastAndroid.SHORT)

    navigation.navigate("Home");
}


const renderProducts = (data, index) => {
    return (
      <TouchableOpacity
        key={data.key}
        onPress={() => navigation.navigate('ProductInfo', {productID: data.id})}
        style={{
          width: '100%',
          height: 100,
          marginVertical: 6,
          flexDirection: 'row',
          alignItems: 'center',
        }}>
        <View
          style={{
            width: '30%',
            height: 100,
            padding: 14,
            justifyContent: 'center',
            alignItems: 'center',
            backgroundColor: COLORS.backgrondLight,
            borderRadius: 10,
            marginRight: 22,
          }}>
          <Image
            source={data.productImage}
            style={{
              width: '100%',
              height: '100%',
              resizeMode: 'contain',
            }}
          />
            </View>
            <View 
                style={{
                    flex:1,
                    height:'100%',
                    justifyContent:'space-around',
                    }}>
                <View style={{}}>
                    <Text 
                        style={{
                            fontSize:14,
                            maxWidth:'100%',
                            color:COLORS.black,
                            fontWeight:'600',
                            letterSpacing:1,

                    }}>
                       {data.productName} 
                    </Text>
                    <View style={{
                        marginTop:4,
                        flexDirection:'row',
                        alignItems:'center',
                        opacity:0.6,
                    }}>
                        <Text style={{
                            fontSize:14,
                            fontWeight:'400',
                            maxWidth:'85%',
                            marginRight:4,
                            fontFamily:'Av'
                        }}>
                            {data.productPrice}$
                        </Text>
                    </View>
                </View>
                <View 
                style={{
                    flexDirection:'row',
                    justifyContent:'space-between',
                    alignItems:'center',
                }}>
                <View 
                style={{
                    flexDirection:'row',
                    justifyContent:'space-between',
                    alignItems:'center',
                }}>
                    <TouchableOpacity
                    style={{
                        borderRadius: 100,
                        marginRight: 20,
                        padding: 4,
                        borderWidth: 1,
                        borderColor:COLORS.backgroundMedium,
                        opacity: 0.5,
                    }}>
                        <MaterialCommunityIcons name = "minus"
                            style={{
                                    fontSize:16,
                                    color:COLORS.backgroundDark,
                        }}/>
                    </TouchableOpacity>
                    <Text>1</Text>
                    <TouchableOpacity 
                    style={{
                        borderRadius: 100,
                        marginLeft: 20,
                        padding: 4,
                        borderWidth: 1,
                        borderColor:COLORS.backgroundMedium,
                        opacity: 0.5,
                    }}>
                        <MaterialCommunityIcons name = "plus"
                            style={{
                                    fontSize:16,
                                    color:COLORS.backgroundDark,
                        }}/>
                    </TouchableOpacity>
                    </View>
                    <TouchableOpacity onPress={()=> removeItemFromCart(data.id)}>
                        <MaterialCommunityIcons 
                            name="delete-outline" 
                            style={{
                                fontSize:23,
                                color:"red",
                                padding:8,
                                borderRadius:100,

                             }}/>
                    </TouchableOpacity>
                </View>
            </View>
        </TouchableOpacity>
    )
}
const [flag, setflag] = useState(false);

const [text, setText] = useState('');
let Discount = 1;
const [ShowDiscount, setShowDiscount] = useState(1);

const checkCoupon = (text) => {
  
 for (const temp of Coupons ) {
  
   if (temp.name === text && flag == false) {
   
    Discount = temp.Discount   
    let price = Discount * total
    setflag(true)
    setShowDiscount(total - price)
    setTotal(price)
    
   
    console.log(ShowDiscount)

    ToastAndroid.show(
      "Coupon was Added Succsesfully!", ToastAndroid.SHORT);
   
   }
 }
