import React, { useEffect, useState } from 'react';
import {
    Text,
     View , 
     StatusBar,
     ScrollView,
     TouchableOpacity , Image ,ImageBackground} from 'react-native';
import { Items} from '../database/Database';
import { COLORS } from '../assets/Style';
import styles from '../assets/Style';
import Entypo from 'react-native-vector-icons/Entypo';
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons';
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import backNike2 from '../assets/images/backNike2.png';
import nike from '../assets/images/nike2.png';
const [product,setProduct] = useState([]);
    const [accessory , setAccessory] = useState([]);
    const [children , setChildren] = useState([]);
    
    useEffect(() =>{
        const unsubscribe = navigation.addListener('focus',()=>{
            getDataFromDB()
        });

        return unsubscribe;
    },[navigation])

    // get data from DB

    const getDataFromDB = () =>{
        let productList = []
        let accessoryList = []
        let childrenList = [];
        
        for (let index = 0; index < Items.length; index++) {
            if(Items[index].category == "product"){
                productList.push(Items[index])
            }else if(Items[index].category == "accessory"){
                accessoryList.push(Items[index]);
            }else if(Items[index].category == "children"){
                childrenList.push(Items[index]);   
            }  
        }

        setProduct(productList);
        setAccessory(accessoryList);
        setChildren(childrenList);
    };

const Home = ({navigation}) =>{
    return(
        <View style={{
            marginTop:30,
            marginBottom:30,
            flexDirection:'row',
            justifyContent:'space-between',
        }}>
            <TouchableOpacity onPress={() => navigation.navigate('Football',)}>
                <Text>Footaball</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => navigation.navigate('Basketball',)}>
                <Text>Basketball</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => navigation.navigate('Jordan',)}>
                <Text>Jordan</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => navigation.navigate('LifeStyle',)}>
                <Text>LifyStyle</Text>
            </TouchableOpacity >
            <TouchableOpacity onPress={() => navigation.navigate('Running',)}>
                <Text>Running</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => navigation.navigate('Soccer',)}>
                <Text>Soccer</Text>
            </TouchableOpacity>
        </View>
    )
    
};

export default Home;
