import React, {useState, useEffect} from 'react';
import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  Image,
  ToastAndroid,
  TextInput,
  StyleSheet,
} from 'react-native';
import styles from '../assets/Style';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { Items} from '../database/Database';
import { COLORS } from '../assets/Style';

const MyCart = ({navigation}) =>{

    const [product, setProduct] = useState();
    const [total , setTotal] = useState(null);

    const [name, setName] = useState();
    const [lastName, setLastName] = useState();
    const [email, setEmail] = useState();
    const [phone, setPhone] = useState();
    const [country, setCountry] = useState();
    const [city, setCity] = useState();
    const [address, setAddress] = useState();


    const [fullName, setFullName] = useState();
    const [id, setId] = useState();
    const [numOfCard, setnumOfCard] = useState();
    const [validity, setValidity] = useState();
    const [CVV, setCVV] = useState();

    useEffect(()=>{
        const unsubscribe = navigation.addListener('focus',()=>{
            getDataFromDB();
        });
        return unsubscribe;
    }, [navigation]);

    const checkCoupon = (text) => {
  
        for (const temp of Coupons ) {
         
          if (temp.name === text && flag == false) {
          
           Discount = temp.Discount   
           let price = Discount * total
           setflag(true)
           setShowDiscount(total - price)
           setTotal(price)
           
          
           console.log(ShowDiscount)
       
           ToastAndroid.show(
             "Coupon was Added Succsesfully!", ToastAndroid.SHORT);
          
          }
        }
        ToastAndroid.show(flag == true? "Coupon already added!" : "Coupon NOT Found!", ToastAndroid.SHORT);
        }
        
    const getDataFromDB = async() =>{
        let items = await AsyncStorage.getItem('cartItems')
        items = JSON.parse(items);
        let productData = [];
        if(items){
            Items.forEach(data =>{
                if(items.includes(data.id)){
                    productData.push(data)
                    return 
                }
            })
            setProduct(productData)
            getTotal(productData)
        }else{
            setProduct(false)
            getTotal(false)
        }
    };
    const getTotal = productData =>{
        let total = 0;
        for (let index = 0; index < productData.length; index++) {
            let productPrice = productData[index].productPrice
            total = total + productPrice; 
        }
        setTotal(total);
    }

// remove data from Cart


 
// checkOut

const checkOut = async () =>{
    try {
        await AsyncStorage.removeItem('cartItems')
    } catch (error) {
        return error
    }

    ToastAndroid.show("Items will be Delivers SOON!" , ToastAndroid.SHORT)

    navigation.navigate("Home");
}


const renderProducts = (data, index) => {
    return (
      <TouchableOpacity
        key={data.key}
        onPress={() => navigation.navigate('ProductInfo', {productID: data.id})}
        style={{
          width: '100%',
          height: 100,
          marginVertical: 6,
          flexDirection: 'row',
          alignItems: 'center',
        }}>
        <View
          style={{
            width: '30%',
            height: 100,
            padding: 14,
            justifyContent: 'center',
            alignItems: 'center',
            backgroundColor: COLORS.backgrondLight,
            borderRadius: 10,
            marginRight: 22,
          }}>
          <Image
            source={data.productImage}
            style={{
              width: '100%',
              height: '100%',
              resizeMode: 'contain',
            }}
          />
            </View>
            <View 
                style={{
                    flex:1,
                    height:'100%',
                    justifyContent:'space-around',
                    }}>
                <View style={{}}>
                    <Text 
                        style={{
                            fontSize:14,
                            maxWidth:'100%',
                            color:COLORS.black,
                            fontWeight:'600',
                            letterSpacing:1,

                    }}>
                       {data.productName} 
                    </Text>
                    <View style={{
                        marginTop:4,
                        flexDirection:'row',
                        alignItems:'center',
                        opacity:0.6,
                    }}>
                        <Text style={{
                            fontSize:14,
                            fontWeight:'400',
                            maxWidth:'85%',
                            marginRight:4,
                        }}>
                            {data.productPrice}$
                        </Text>
                    </View>
                </View>
                <View 
                style={{
                    flexDirection:'row',
                    justifyContent:'space-between',
                    alignItems:'center',
                }}>
                <View 
                style={{
                    flexDirection:'row',
                    justifyContent:'space-between',
                    alignItems:'center',
                }}>
                    <View 
                    style={{
                        borderRadius: 100,
                        marginRight: 20,
                        padding: 4,
                        borderWidth: 1,
                        borderColor:COLORS.backgroundMedium,
                        opacity: 0.5,
                    }}>
                    </View>
                    <Text>1</Text>
                    <View 
                    style={{
                        borderRadius: 100,
                        marginLeft: 20,
                        padding: 4,
                        borderWidth: 1,
                        borderColor:COLORS.backgroundMedium,
                        opacity: 0.5,
                    }}>
                    </View>
                    </View>
                </View>
            </View>
        </TouchableOpacity>
    )
}

return(
    <View style={{
        width:'100%',
        height:'100%',
        backgroundColor:COLORS.white,
        position:'relative',
    }}>
        <ScrollView>
            <View style={{
                width:'100%',
                flexDirection:'row',
                paddingTop:16,
                justifyContent:'space-between',
                alignItems:'center',

            }}>
                {/* row left - goBack */}
                <TouchableOpacity onPress={() => navigation.goBack()}> 
                </TouchableOpacity>
            </View>
            <Text 
            style={{
                fontSize:20,
                color:COLORS.black,
                fontWeight:'500',
                letterSpacing:1,
                paddingTop:20,
                paddingLeft:16,
                marginBottom:10,
                fontFamily:'DancingScript-Regular',
            }}>
                Please fill in the details
                {'\n'}to arrange payment 
            </Text>
            <View style={{paddingHorizontal:16}}>
                {product ? product.map(renderProducts) : null}
            </View>
            <View>
            <View style={{
                paddingHorizontal:16,
                marginVertical:10,
            }}>
                <View style={{
                    flexDirection:'row',
                    alignItems:'center',
                    justifyContent:'space-between',
                }}>
                    <View style={{
                        flexDirection:'row',
                        width:'80%',
                        alignItems:'center',
                    }}>
                    </View>
                </View>
                </View>
                <View style={{
                paddingHorizontal:16,
                marginVertical:10,
            }}>
                <View style={{
                    flexDirection:'row',
                    alignItems:'center',
                    justifyContent:'space-between',
                }}>
                    <View style={{
                        flexDirection:'row',
                        width:'80%',
                        alignItems:'center',
                    }}>  
                    </View>
                </View>
                </View>

                {/* Text Input for Pay */}

                <View style={{
                    alignItems:'center',
                    
                }}>
                    <Text style={{
                        fontSize:18,
                        color:COLORS.black,
                        fontWeight:'1',
                        letterSpacing:10,
                        paddingTop:20,
                        paddingLeft:16,
                        marginBottom:10,  
                    }}>User Information</Text>
                    <TextInput 
                    style={styles.input}
                    placeholder='First Name'
                    />
                    <TextInput 
                    style={styles.input}
                    placeholder='Last Name'
                    />
                    <TextInput 
                    style={styles.input}
                    placeholder='Email'
                    />
                    <TextInput 
                    style={styles.input} keyboardType="phone-pad"
                    placeholder='Phone Number'
                    />
                    <TextInput 
                    style={styles.input}
                    placeholder='Country'
                    />
                    <TextInput 
                    style={styles.input}
                    placeholder='City'
                    />
                    <TextInput 
                    style={styles.input}
                    placeholder='Address'
                    />
                    <Text style={{
                        fontSize:18,
                        color:COLORS.black,
                        fontWeight:'1',
                        letterSpacing:10,
                        paddingTop:20,
                        paddingLeft:16,
                        marginBottom:10, 
                    }}>Details Payment </Text>
                    <TextInput 
                    style={styles.input}
                    placeholder='Full Name'
                    />
                    <TextInput 
                    style={styles.input}
                    placeholder='ID Number'
                    />
                    <TextInput 
                    style={styles.input} maxLength={12}
                    placeholder='Card Number'
                    />
                    <TextInput 
                    style={styles.input}
                    placeholder='validity'
                    />
                    <TextInput 
                    style={styles.input} maxLength={3}
                    placeholder='CVV'
                    />
                </View>
                {/* Order Info */}
                
                <View style={{
                    paddingHorizontal:16,
                    marginTop:40,
                    marginBottom:80,
                }}>
                    <Text style={{
                        fontSize:16,
                        color:COLORS.black,
                        fontWeight:'500',
                        letterSpacing:1,
                        marginBottom:20,
                    }}>
                        Order Info
                    </Text>
                    <View style={{
                        flexDirection:'row',
                        alignItems:'center',
                        justifyContent:'space-between',
                        marginBottom:8,
                    }}>
                        <Text 
                        style={{
                            fontSize:12,
                            fontWeight:'400',
                            maxWidth:'80%',
                            color:COLORS.black,
                            opacity:0.5,
                        }}> Subtotal</Text>
                        <Text 
                        style={{
                            fontSize:12,
                            fontWeight:'400',
                            maxWidth:'80%',
                            color:COLORS.black,
                            opacity:0.8,
                        }}> 
                        {total}.00$ 
                        </Text>
                    </View>
                    <View style={{
                        flexDirection:'row',
                        alignItems:'center',
                        justifyContent:'space-between',
                        marginBottom:22,
                    }}>
                        <Text 
                        style={{
                            fontSize:12,
                            fontWeight:'400',
                            maxWidth:'80%',
                            color:COLORS.black,
                            opacity:0.5,
                        }}> Shipping Tax</Text>
                        <Text 
                        style={{
                            fontSize:12,
                            fontWeight:'400',
                            maxWidth:'80%',
                            color:COLORS.black,
                            opacity:0.8,
                        }}> 
                        {total / 20}$ 
                        </Text>
                    </View>
                    <View style={{
                        flexDirection:'row',
                        alignItems:'center',
                        justifyContent:'space-between',
                    }}>
                        <Text 
                        style={{
                            fontSize:12,
                            fontWeight:'400',
                            maxWidth:'80%',
                            color:COLORS.black,
                            opacity:0.5,
                        }}> Total</Text>
                        <Text 
                        style={{
                            fontSize:18,
                            fontWeight:'500',
                            color:COLORS.black,
                            
                        }}> 
                        {total + total / 20}$ 
                        </Text>
                    </View>
                </View>
            </View>
        </ScrollView>
        
        <View
        style={{
          position: 'absolute',
          bottom: 10,
          height: '8%',
          width: '100%',
          justifyContent: 'center',
          alignItems: 'center',
        }}>
        <TouchableOpacity
          onPress={() => navigation.navigate('Succsess',)}
          style={{
            width: '86%',
            height: '90%',
            backgroundColor: COLORS.blue,
            borderRadius: 20,
            justifyContent: 'center',
            alignItems: 'center',
          }}>
          <Text
            style={{
              fontSize: 12,
              fontWeight: '500',
              letterSpacing: 1,
              color: COLORS.white,
              textTransform: 'uppercase',
            }}>
                Buy  {total + total / 20}$
          </Text>
        </TouchableOpacity>
      </View>
    </View>
    );
};

export default MyCart;
